import {Sync,Control} from 'gfs-react-mvc'

class TestControl{
    constructor(){}
    //这里由于@为文档关键符号，所以下面将以$代替
    $Sync('/test',{
        method:'get'
    })
    save(data){
        //此处data是异步请求后服务器返回的结果
        if(data){
            //返回数据更新页面节点信息
            return data
        }
        //不做任何改变
        return false
    } 
    $Sync('/del',{
        method:'get'
    })
    del(data){
        //此处data是异步请求后服务器返回的结果
        if(data){
            //返回数据更新页面节点信息
            return {
                //手动指定model对应方法
                type:this.getModelName('del'),
                data:data
            }
        }
        //不做任何改变
        return false
    }
    //也可直接使用
    $Sync()
    update(data){
        //此处data是异步请求后服务器返回的结果
        if(data){
            //返回数据更新页面节点信息
            return {
                //手动指定model对应方法
                type:this.getModelName('update'),
                data:data
            }
        }
        //不做任何改变
        return false
    }
}

